ArrayList translatePermissionMask(def mask, def params) {
  ArrayList al = new ArrayList();
  Long permCode = Long.decode(mask);
  for (entry in params.PermsFlags.entrySet()) {
    Long permFlag = Long.decode(entry.getKey());
    if ((permCode.longValue() & permFlag.longValue()) == permFlag.longValue()) {
      al.add(entry.getValue());
    }
  }
  if (al.length == 0) {
    al.add(mask);
  }
  return al;
}

HashMap translateACL(def dacl, def params) {
  def aceArray = dacl.splitOnToken(";");
  HashMap hm = new HashMap();

  if (aceArray.length >= 6 ) {
    hm.put("grantee", translateSID(aceArray[5], params));
  }

  if (aceArray.length >= 1) {
    hm.put("type", params.AceTypes[aceArray[0]]);
  }

  if (aceArray.length >= 3) {
    if (aceArray[2].startsWith("0x")) {
      hm.put("perms", translatePermissionMask(aceArray[2], params));
    } else {
      ArrayList al = new ArrayList();
      Pattern permPattern = /.{1,2}/;
      Matcher permMatcher = permPattern.matcher(aceArray[2]);
      while (permMatcher.find()) {
        al.add(params.PermissionDescription[permMatcher.group(0)]);
      }
      hm.put("perms", al);
    }
  }
  return hm;
}
String translateSID(def sid, def params) {
  if (!params.AccountSIDDescription.containsKey(sid)) {
    if (sid.startsWith("S-1-5-21")) {
      Pattern uidPattern = /[0-9]{1,5}$/;
      Matcher uidMatcher = uidPattern.matcher(sid);
      if (uidMatcher.find()) {
        return params.DomainSpecificSID[uidMatcher.group(0)];
      }
      return sid;
    }
    return sid;
  }
  return params.AccountSIDDescription[sid];
}

void enrichSDDL(def sddlStr, def Sd, def params, def ctx) {
  Pattern sdOwnerPattern = /^O\:[A-Z]{2}/;
  Matcher sdOwnerMatcher = sdOwnerPattern.matcher(sddlStr);
  if (sdOwnerMatcher.find()) {
    ctx.winlog.event_data.put(Sd + "Owner", translateSID(sdOwnerMatcher.group(0), params));
  }

  Pattern sdGroupPattern = /^G\:[A-Z]{2}/;
  Matcher sdGroupMatcher = sdGroupPattern.matcher(sddlStr);
  if (sdGroupMatcher.find()) {
    ctx.winlog.event_data.put(Sd + "Group", translateSID(sdGroupMatcher.group(0), params));
  }

  Pattern sdDaclPattern = /(D:([A-Z]*(\(.*\))*))/;
  Matcher sdDaclMatcher = sdDaclPattern.matcher(sddlStr);
  if (sdDaclMatcher.find()) {
    Pattern dacListPattern = /\([^*\)]*\)/;
    Matcher dacListMatcher = dacListPattern.matcher(sdDaclMatcher.group(1));
    for (def i = 0; dacListMatcher.find(); i++) {
      def newDacl = translateACL(dacListMatcher.group(0).replace("(","").replace(")",""), params);
      ctx.winlog.event_data.put(Sd + "Dacl" + i.toString(), newDacl['grantee'] + " :" + newDacl['type'] + " (" + newDacl['perms'] + ")");
      if (["Administrator", "Guest", "KRBTGT"].contains(newDacl['grantee'])) {
        if (ctx?.related == null) {
          HashMap hm = new HashMap();
          ctx.put("related", hm);
        }
        if (ctx?.related?.user == null) {
          ArrayList al = new ArrayList();
          ctx.related.put("user", al);
        }
        if (!ctx.related.user.contains(newDacl['grantee'])) {
          ctx.related.user.add(newDacl['grantee']);
        }
      }
    }
  }

  Pattern sdSaclPattern = /(S:([A-Z]*(\(.*\))*))?$/;
  Matcher sdSaclMatcher = sdSaclPattern.matcher(sddlStr);
  if (sdSaclMatcher.find()) {
    Pattern sacListPattern = /\([^*\)]*\)/;
    Matcher sacListMatcher = sacListPattern.matcher(sdSaclMatcher.group(0));
    for (def i = 0; sacListMatcher.find(); i++) {
      def newSacl = translateACL(sacListMatcher.group(0).replace("(","").replace(")",""), params);
      ctx.winlog.event_data.put(Sd + "Sacl" + i.toString(), newSacl['grantee'] + " :" + newSacl['type'] + " (" + newSacl['perms'] + ")");
      if (["Administrator", "Guest", "KRBTGT"].contains(newSacl['grantee'])) {
        if (ctx?.related == null) {
          HashMap hm = new HashMap();
          ctx.put("related", hm);
        }
        if (ctx?.related?.user == null) {
          ArrayList al = new ArrayList();
          ctx.related.put("user", al);
        }
        if (!ctx.related.user.contains(newSacl['grantee'])) {
          ctx.related.user.add(newSacl['grantee']);
        }
      }
    }
  }
}

def split(String s) {
  def f = new ArrayList();
  int last = 0;
  for (; last < s.length() && Character.isWhitespace(s.charAt(last)); last++) {}
  for (def i = last; i < s.length(); i++) {
    if (!Character.isWhitespace(s.charAt(i))) {
      continue;
    }
    f.add(s.substring(last, i));
    for (; i < s.length() && Character.isWhitespace(s.charAt(i)); i++) {}
    last = i;
  }
  f.add(s.substring(last));
  return f;
}

void splitSidList(def sids, def params, def ctx) {
  ArrayList list = new ArrayList();
  ArrayList desc = new ArrayList();
  def sidList = split(sids);
  for (def i = 0; i < sidList.length; i++) {
    list.add(sidList[i]);
    desc.add(translateSID(sidList[i].replace("%", "").replace("{", "").replace("}", ""), params));
  }
  ctx.winlog.event_data.put("SidList", list);
  ctx.winlog.event_data.put("SidListDesc", desc);
}

